INSERT INTO `t_music` (`id`, `name`, `cover`, `artist`, `genre`, `description`, `file_url`, `created_at`, `image`) VALUES (14,'百鸟朝凤',NULL,'魏子猷','纯音乐，唢呐独奏','《百鸟朝凤》外文名为《Hundreds Of Birds Worshipping The Phoenix》，是由民乐大师魏子猷原创，流行于河南、安徽、山东、河北等的民族乐曲。其前身是豫剧抬花轿中的伴奏曲，因为豫剧流行很广，使百鸟朝凤流行于河南、山东、河北、安徽等地，又名《百鸟音》，这是一首充分展示唢呐艺术魅力的优秀乐曲。','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/777bcd949c9b412e8731e2b5836ee314/百鸟朝凤片段.MP3','2023-04-01 14:28:18','https://s2.loli.net/2023/04/10/ewD2kGJUxVcipNX.png'),(15,'梅花三弄',NULL,'晋朝桓伊','古曲','《梅花三弄》，又名《梅花引》、《梅花曲》、《玉妃引》，根据《太音补遗》和《蕉庵琴谱》所载，相传原本是晋朝桓伊所作的一首笛曲，后来改编为古琴曲。琴曲的乐谱最早见于公元1425年的《神奇秘谱》。《梅花三弄》全曲共分十段，两大部分，第一部分，前六段，采用循环再现手法，后四段为第二部分，描写梅花静与动两种形象。乐曲通过梅花的洁白芬芳和耐寒等特征，借物抒怀，来歌颂具有高尚节操的人。','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/d152aa71e5dd4e4d9411917df42c143f/梅花三弄片段.MP3','2023-04-01 14:29:08','https://s2.loli.net/2023/04/10/CWsZX8DfKUh7uAN.png'),(16,'茉莉花',NULL,'何仿(改编)','民调','《茉莉花》由何仿改编自中国民歌《鲜花调》，于1957年首次以单曲形式发行。该曲属于小调类民歌，是单乐段的歌曲。它以五声调式和级进的旋律，表现了委婉流畅、柔和与优美的江南风格，生动刻画了一个文雅贤淑的少女被芬芳美丽的茉莉花所吸引，欲摘不忍，欲弃不舍的爱慕和眷恋之情。全曲婉转精美，感情深厚又含蓄。','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/283385e4d1494b2eb0c5722d826cb9f9/茉莉花片段.MP3','2023-04-01 14:29:54','https://s2.loli.net/2023/04/10/PUT4wAyERKvWoV9.png'),(17,'牛斗虎片段',NULL,'民调','民调','牛斗虎是一种模拟动物形体、习性的传统民间舞蹈。流传于盂县一带。相传这种舞蹈是根据当地流传的一则民间传说而起源。牛斗虎以牛虎争斗为内容，形象地刻画了老虎的凶猛威武，又颂扬了老牛忠厚倔强。','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/e8c4abd9027a4f9aa4339ca5ea69ff17/牛斗虎片段.MP3','2023-04-01 14:30:29','https://s2.loli.net/2023/04/10/OCpM15NcAstmul7.png'),(18,'高山流水',NULL,'(先秦)伯牙','纯音乐','《高山流水》，中国古琴曲，属于中国十大古曲之一。相传先秦的琴师伯牙一次于山野间弹琴，樵夫钟子期竟能领会这是描绘“峨峨兮若泰山”和“洋洋兮若江河”。伯牙惊道：“善哉，子之心而与吾心同。”钟子期死后，伯牙痛失知音，摔琴绝弦，终生不弹，故有高山流水之曲。','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/318cdfb8b79841ada3fea6adb6ab83fd/高山流水片段.MP3','2023-04-01 14:31:04','https://s2.loli.net/2023/04/10/8bnBdThCaHrXPeA.png'),(19,'十面埋伏',NULL,'汤应曾','琵琶独奏','《十面埋伏》，又名《淮阴平楚》，是以楚汉相争的历史为题材而创作的琵琶独奏曲。乐曲整体可分为三部分，由十三段带有小标题的段落构成，分别是：列营、吹打、点将、排阵、走队、埋伏、鸡鸣山小战、九里山大战、项王败阵、乌江自刎、众军奏凯、诸将争功和得胜回营。以公元前202年刘邦与项羽垓下之战的史实为内容，用标题音乐的形式描绘了激烈的战争场面。','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/79868c4362d04adabdb7f39b78f91a2e/十面埋伏片段.MP3','2023-04-01 14:31:27','https://s2.loli.net/2023/04/10/kc4gYNAHtpwTuab.png'),(20,'终南古韵',NULL,'宁勇','中阮独奏','中阮独奏曲。','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/65daf30d0753497485a786c4be07c748/钟南古韵片段.MP3','2023-04-01 14:31:59','https://s2.loli.net/2023/04/10/xhsQLjMJ68teGi2.png'),(21,'二泉映月',NULL,'阿炳','二胡独奏','《二泉映月》，是中国民间音乐家华彦钧（阿炳）的代表作。这首乐曲自始至终流露的是一位饱尝人间辛酸和痛苦的盲艺人的思绪情感，作品展示了独特的民间演奏技巧与风格，以及无与伦比的深邃意境，显示了中国二胡艺术的独特魅力，它拓宽了二胡艺术的表现力，曾获“20世纪华人音乐经典作品奖”。','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/f6e040e23e504e9091a64199b233f889/二泉映月片段.MP3','2023-04-01 14:32:29','https://s2.loli.net/2023/04/10/SLgdMNY4XJkRQOP.png'),(22,'夜深沉',NULL,'无','戏曲','《夜深沉》是一个京剧曲牌名。曲调由繁至简，在快板段落作了较多发展，其中有大鼓的独奏及鼓与京胡的竞奏，使原曲的精华--刚劲且优美的音乐得到充分优美的表现。在京剧《击鼓骂曹》和《霸王别姬》中，用 它来配合祢衡击鼓和虞姬舞剑等的场面。','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/c60a858d52e74dee811ded6454c91c40/夜深沉片段.MP3','2023-04-01 14:32:52','https://s2.loli.net/2023/04/10/ifh4dBIR5O2jlYH.png'),(23,'春江花月夜','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/39d14107f3f14a7f9356f77ed04eb114/image.jpg','黄自','古琴曲','春江花月夜','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/c8b1e16d2cef43a1b3899a57a462f618/春江花月夜.mp3','2023-04-27 11:21:57','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/39d14107f3f14a7f9356f77ed04eb114/image.jpg'),(24,'春江花月夜','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/c112a0297bbd4ac6bdb4515dbef61306/image.jpg','黄自','古琴曲','春江花月夜','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/8cbc9af43cd0407e8731c9012b7627c2/春江花月夜.mp3','2023-04-27 11:23:14','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/c112a0297bbd4ac6bdb4515dbef61306/image.jpg'),(25,'二泉映月','https://musicstyle.oss-cn-shanghai.aliyuncs.com/files/55a3e1a7ffb44a9f963d9cb16647fb0a/image.jpg','阿炳','二胡独奏','《二泉映月》，是中国民间音乐家华彦钧（阿炳）的代表作。这首乐曲自始至终流露的是一位饱尝人间辛酸和痛苦的盲艺人的